Smart::StatusCode OneUser::connectBWMap(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = bWMap->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->bWMap->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	bWMap->subscribe(connections.bWMap.interval);
	return status;
}
Smart::StatusCode OneUser::connectMap(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = map->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->map->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	map->subscribe(connections.map.interval);
	return status;
}


/**
 * First connect ALL client ports contained in this component, then start all services:
 * activate state, push, etc...
 */
Smart::StatusCode OneUser::connectAndStartAllServices() {
	Smart::StatusCode status = Smart::SMART_OK;
	
	status = connectBWMap(connections.bWMap.serverName, connections.bWMap.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectMap(connections.map.serverName, connections.map.serviceName);
	if(status != Smart::SMART_OK) return status;
	return status;
}

/**
 * Start all tasks contained in this component.
 */
void OneUser::startAllTasks() {
	// start task ImageViewer
	if(connections.imageViewer.scheduler != "DEFAULT") {
		ACE_Sched_Params imageViewer_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.imageViewer.scheduler == "FIFO") {
			imageViewer_SchedParams.policy(ACE_SCHED_FIFO);
			imageViewer_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.imageViewer.scheduler == "RR") {
			imageViewer_SchedParams.policy(ACE_SCHED_RR);
			imageViewer_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		imageViewer->start(imageViewer_SchedParams, connections.imageViewer.cpuAffinity);
	} else {
		imageViewer->start();
	}
}

