Smart::TaskTriggerSubject* OneUser::getInputTaskTriggerFromString(const std::string &client)
{
	if(client == "BWMap") return bWMapInputTaskTrigger;
	if(client == "Map") return mapInputTaskTrigger;
	
	return NULL;
}


