/**
 * Start all tasks contained in this component.
 */
void ZeroCamera::startAllTasks() {
	// start task ImageCapture
	if(connections.imageCapture.scheduler != "DEFAULT") {
		ACE_Sched_Params imageCapture_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.imageCapture.scheduler == "FIFO") {
			imageCapture_SchedParams.policy(ACE_SCHED_FIFO);
			imageCapture_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.imageCapture.scheduler == "RR") {
			imageCapture_SchedParams.policy(ACE_SCHED_RR);
			imageCapture_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		imageCapture->start(imageCapture_SchedParams, connections.imageCapture.cpuAffinity);
	} else {
		imageCapture->start();
	}
	// start task rgb2gray
	if(connections.rgb2gray.scheduler != "DEFAULT") {
		ACE_Sched_Params rgb2gray_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.rgb2gray.scheduler == "FIFO") {
			rgb2gray_SchedParams.policy(ACE_SCHED_FIFO);
			rgb2gray_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.rgb2gray.scheduler == "RR") {
			rgb2gray_SchedParams.policy(ACE_SCHED_RR);
			rgb2gray_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		rgb2gray->start(rgb2gray_SchedParams, connections.rgb2gray.cpuAffinity);
	} else {
		rgb2gray->start();
	}
}

