void ZeroCamera::loadParameter(int argc, char *argv[])
{
	/*
	 Parameters can be specified via command line --filename=<filename> or -f <filename>

	 With this parameter present:
	   - The component will look for the file in the current working directory,
	     a path relative to the current directory or any absolute path
	   - The component will use the default values if the file cannot be found

	 With this parameter absent:
	   - <Name of Component>.ini will be read from current working directory, if found there
	   - $SMART_ROOT/etc/<Name of Component>.ini will be read otherwise
	   - Default values will be used if neither found in working directory or /etc
	 */
	SmartACE::SmartIniParameter parameter;
	std::ifstream parameterfile;
	bool parameterFileFound = false;

	// load parameters
	try
	{
		// if paramfile is given as argument
		if(parameter.tryAddFileFromArgs(argc,argv,"filename", 'f'))
		{
			parameterFileFound = true;
			std::cout << "parameter file is loaded from an argv argument \n";
		} else if(parameter.searchFile("ZeroCamera.ini", parameterfile)) {
			parameterFileFound = true;
			std::cout << "load ZeroCamera.ini parameter file\n";
			parameter.addFile(parameterfile);
		} else {
			std::cout << "WARNING: ZeroCamera.ini parameter file not found! (using default values or command line arguments)\n";
		}
		
		// add command line arguments to allow overwriting of parameters
		// from file
		parameter.addCommandLineArgs(argc,argv,"component");
		
		// initialize the naming service using the command line parameters parsed in the
		// SmartIniParameter class. The naming service parameters are expected to be in
		// the "component" parameter group.
		SmartACE::NAMING::instance()->checkForHelpArg(argc,argv);
		if(parameterFileFound) 
		{
			if(SmartACE::NAMING::instance()->init(parameter.getAllParametersFromGroup("component")) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		} else {
			if(SmartACE::NAMING::instance()->init(argc, argv) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		}
			
		// print all known parameters
		// parameter.print();
		
		//--- server port // client port // other parameter ---
		// load parameter
		parameter.getString("component", "name", connections.component.name);
		parameter.getString("component", "initialComponentMode", connections.component.initialComponentMode);
		if(parameter.checkIfParameterExists("component", "defaultScheduler")) {
			parameter.getString("component", "defaultScheduler", connections.component.defaultScheduler);
		}
		if(parameter.checkIfParameterExists("component", "useLogger")) {
			parameter.getBoolean("component", "useLogger", connections.component.useLogger);
		}
		
		
		// load parameters for server GrayImage
		parameter.getString("GrayImage", "serviceName", connections.grayImage.serviceName);
		if(parameter.checkIfParameterExists("GrayImage", "roboticMiddleware")) {
			parameter.getString("GrayImage", "roboticMiddleware", connections.grayImage.roboticMiddleware);
		}
		// load parameters for server RGBImage
		parameter.getString("RGBImage", "serviceName", connections.rGBImage.serviceName);
		if(parameter.checkIfParameterExists("RGBImage", "roboticMiddleware")) {
			parameter.getString("RGBImage", "roboticMiddleware", connections.rGBImage.roboticMiddleware);
		}
		
		// load parameters for task ImageCapture
		parameter.getDouble("ImageCapture", "minActFreqHz", connections.imageCapture.minActFreq);
		parameter.getDouble("ImageCapture", "maxActFreqHz", connections.imageCapture.maxActFreq);
		parameter.getString("ImageCapture", "triggerType", connections.imageCapture.trigger);
		if(connections.imageCapture.trigger == "PeriodicTimer") {
			parameter.getDouble("ImageCapture", "periodicActFreqHz", connections.imageCapture.periodicActFreq);
		} else if(connections.imageCapture.trigger == "DataTriggered") {
			parameter.getString("ImageCapture", "inPortRef", connections.imageCapture.inPortRef);
			parameter.getInteger("ImageCapture", "prescale", connections.imageCapture.prescale);
		}
		if(parameter.checkIfParameterExists("ImageCapture", "scheduler")) {
			parameter.getString("ImageCapture", "scheduler", connections.imageCapture.scheduler);
		}
		if(parameter.checkIfParameterExists("ImageCapture", "priority")) {
			parameter.getInteger("ImageCapture", "priority", connections.imageCapture.priority);
		}
		if(parameter.checkIfParameterExists("ImageCapture", "cpuAffinity")) {
			parameter.getInteger("ImageCapture", "cpuAffinity", connections.imageCapture.cpuAffinity);
		}
		// load parameters for task rgb2gray
		parameter.getDouble("rgb2gray", "minActFreqHz", connections.rgb2gray.minActFreq);
		parameter.getDouble("rgb2gray", "maxActFreqHz", connections.rgb2gray.maxActFreq);
		parameter.getString("rgb2gray", "triggerType", connections.rgb2gray.trigger);
		if(connections.rgb2gray.trigger == "PeriodicTimer") {
			parameter.getDouble("rgb2gray", "periodicActFreqHz", connections.rgb2gray.periodicActFreq);
		} else if(connections.rgb2gray.trigger == "DataTriggered") {
			parameter.getString("rgb2gray", "inPortRef", connections.rgb2gray.inPortRef);
			parameter.getInteger("rgb2gray", "prescale", connections.rgb2gray.prescale);
		}
		if(parameter.checkIfParameterExists("rgb2gray", "scheduler")) {
			parameter.getString("rgb2gray", "scheduler", connections.rgb2gray.scheduler);
		}
		if(parameter.checkIfParameterExists("rgb2gray", "priority")) {
			parameter.getInteger("rgb2gray", "priority", connections.rgb2gray.priority);
		}
		if(parameter.checkIfParameterExists("rgb2gray", "cpuAffinity")) {
			parameter.getInteger("rgb2gray", "cpuAffinity", connections.rgb2gray.cpuAffinity);
		}
		
		// load parameters for PlainOpcUaZeroCameraExtension
		
		
		// load parameters for all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->loadParameters(parameter);
		}
		
	
	} catch (const SmartACE::IniParameterError & e) {
		std::cerr << e.what() << std::endl;
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std::exception: " << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}
